-- RELAÇÃO DE ENDEREÇO
INSERT INTO t_location_relation (wh_id, location_id, related_location_id, relation_type, zone, status)
	SELECT 
		st.wh_id
		, 'CTN_LJ_'||SUBSTR('LOJA'||LPAD(st.store_number,3,'0'),5,3)||'.1' as NOVO_ENDERECO
		, 'EST_STG_SS'
		, 'CARTON_SHIP'
		, 'ALL'
		, 'ALL'
	 FROM t_store_detail st
	 where store_number in ('230','239','257','268',
	'305','317','355','362',
	'383','428','457','468',
	'92','107','133','191',
	'199','249','261','262',
	'276')
	-- GARATIR QUE ENDEREÇOS ESTEJAM CADASTRADO
	AND NOT EXISTS (SELECT 1 FROM t_location_relation
				WHERE location_id = 'CTN_LJ_'||SUBSTR('LOJA'||LPAD(st.store_number,3,'0'),5,3)||'.1'
				AND     wh_id = st.wh_id);
  
  
-- CADASTRO DE ZONA (ALL) - Para os tres endereços da Loja
INSERT INTO t_zone_loca (wh_id, zone, location_id)
	SELECT 
		st.wh_id
		, 'ALL'
		, 'CTN_LJ_'||SUBSTR('LOJA'||LPAD(st.store_number,3,'0'),5,3)||'.3' as NOVO_ENDERECO
	 FROM t_store_detail st
	 where store_number in ('22','51','52','57','98','109','150','480')
	-- GARATIR QUE ENDEREÇOS ESTEJAM CADASTRADO
	AND NOT EXISTS (SELECT 1 FROM t_zone_loca
				WHERE location_id = 'CTN_LJ_'||SUBSTR('LOJA'||LPAD(st.store_number,3,'0'),5,3)||'.3'
				AND     wh_id = st.wh_id);
				
				
